mapper.update:
update province
set name = #{name,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  parent_code = #{parent_code,jdbcType=VARCHAR}
where code = #{code,jdbcType=VARCHAR}

mapper.sql:
from province t left join province a on t.parent_code=a.code where 0=0
and t.name like concat(#{condition.name,jdbcType=VARCHAR},'%')
and t.code = #{condition.code,jdbcType=VARCHAR}
and t.parent_code = #{condition.parent_code,jdbcType=VARCHAR}

mapper.select:
select
t.code,t.name, a.name parentName,t.parent_code



    ORDER BY t.${orderBy}


    ORDER BY t.code


limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
