mapper.sql:
from tz.file t
where 0=0

    and t.name like  concat('%',#{condition.name,jdbcType=VARCHAR},'%')



    and t.remark like concat(#{condition.remark,jdbcType=VARCHAR},'%')




    and t.type =#{condition.type,jdbcType=VARCHAR}



    and t.suffix =#{condition.suffix,jdbcType=VARCHAR}

mapper.select:
select m.* from( select t.id, t.name, t.remark, t.type, t.file, t.suffix, t.fileSize, t.createUserId, t.createUserName, t.createDate, t.lastModifyUserId, t.lastModifyUserName, t.lastModifyDate
) m
ORDER BY m.${orderBy}
order by lastModifyDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
